plot_leaf_columns <- function(df, variable) {
    v <- enquo(variable)
    pal <- colorFactor(palette = "plasma", domain = pull(df, !!v) )

    rlang::eval_tidy(rlang::quo_squash(quo({
        df %>% 
            leaflet(width = "100%") %>% 
            addPolygons(color = ~pal(!!v))
    })))
}

